from tests.unit import BaseAWSCommandParamsTest
import os
import re

from six.moves import cStringIO
import mock
import httpretty


BODY = """<CreateVirtualMFADeviceResponse xmlns="https://iam.amazonaws.com/doc/2010-05-08/">
  <CreateVirtualMFADeviceResult>
    <VirtualMFADevice>
      <QRCodePNG></QRCodePNG>
      <Base32StringSeed>VFpYTVc2V1lIUFlFRFczSVhLUlpRUTJRVFdUSFRNRDNTQ0c3TkZDUVdQWDVETlNWM0IyUENaQVpWTEpQTlBOTA==</Base32StringSeed>
      <SerialNumber>arn:aws:iam::419278470775:mfa/fiebaz</SerialNumber>
    </VirtualMFADevice>
  </CreateVirtualMFADeviceResult>
  <ResponseMetadata>
    <RequestId>3d127f08-0f41-11e3-941e-8d1b33bbf528</RequestId>
  </ResponseMetadata>
</CreateVirtualMFADeviceResponse>"""

class TestCreateVirtualMFADevice(BaseAWSCommandParamsTest):

    prefix = 'iam create-virtual-mfa-device'

    def register_uri(self):
        httpretty.register_uri(httpretty.POST, re.compile('.*'), body=BODY)

    def getpath(self, filename):
        return os.path.join(os.path.abspath(os.path.dirname(__file__)),
                            filename)

    def remove_file_if_exists(self, filename):
        if os.path.isfile(filename):
            os.remove(filename)

    def test_base32(self):
        outfile = self.getpath('fiebaz.b32')
        self.addCleanup(self.remove_file_if_exists, outfile)
        cmdline = self.prefix
        cmdline += ' --virtual-mfa-device-name fiebaz'
        cmdline += ' --outfile %s --bootstrap-method Base32StringSeed' % outfile
        result = {"VirtualMFADeviceName": 'fiebaz'}
        self.assert_params_for_cmd(cmdline, result)
        self.assertTrue(os.path.exists(outfile))

    def test_qrcode(self):
        outfile = self.getpath('fiebaz.png')
        self.addCleanup(self.remove_file_if_exists, outfile)
        cmdline = self.prefix
        cmdline += ' --virtual-mfa-device-name fiebaz'
        cmdline += ' --outfile %s --bootstrap-method QRCodePNG' % outfile
        result = {"VirtualMFADeviceName": 'fiebaz'}
        self.assert_params_for_cmd(cmdline, result)
        self.assertTrue(os.path.exists(outfile))

    def test_bad_filename(self):
        captured = cStringIO()
        outfile = '/some/bad/filename.png'
        self.addCleanup(self.remove_file_if_exists, outfile)
        cmdline = self.prefix
        cmdline += ' --virtual-mfa-device-name fiebaz'
        cmdline += ' --outfile %s --bootstrap-method QRCodePNG' % outfile
        result = {}
        with mock.patch('sys.stderr', captured):
            self.assert_params_for_cmd(cmdline, result, expected_rc=255)
        
